
public class breakandcontinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int j = 1; j<=10-1; j++)
		{
			if(j==5)
			{
				//break; // breaks the code when it reaches 5
				continue; //continue statement skips 5 and prints rest of the numbers
				
			}
			System.out.print(j);
		}

	}

}
